import { Component, OnInit } from '@angular/core';
import { CrudUserService } from 'src/app/services/crud-user.service';
import { User } from 'src/app/interfaces/user';
import { Observable, Subject } from 'rxjs';
import {
  debounceTime, distinctUntilChanged, switchMap
} from 'rxjs/operators';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})

export class UsersComponent implements OnInit {

  private users: Observable<User[]>;

  constructor(private crudService: CrudUserService) { }

  ngOnInit(): void {
    this.getAllUsers()
  }

  getAllUsers () {
    this.crudService.showUsers().subscribe({
      next(users) {
        console.log('tous les utilisateurs: ', users);
        return users
      },
      error(msg) {
        console.log('Error Getting users: ', msg);
      }
    })
  }
}
<h2>Liste des utilisateurs</h2>
<ul>
  <li *ngFor="let user of showUsers() | async">{{ user.email }}</li>
</ul>
