import { Component, OnInit } from '@angular/core';
import { CrudUserService } from 'src/app/services/crud-user.service';
import { User } from 'src/app/interfaces/user';
import { Observable, Subject } from 'rxjs';
import {
  debounceTime, distinctUntilChanged, switchMap
} from 'rxjs/operators';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})

export class UsersComponent implements OnInit {

  private users: Observable<User[]>;
  private searchTerms = new Subject<string>();

  constructor(private crudService: CrudUserService) { }

  // Push a search term into the observable stream.
  search(term: string): void {
    this.searchTerms.next(term);
  }

  ngOnInit(): void {
    this.users = this.searchTerms.pipe(
      // wait 300ms after each keystroke before considering the term
      debounceTime(300),

      // ignore new term if same as previous term
      distinctUntilChanged(),

      // switch to new search observable each time the term changes
      switchMap((term: string) => this.crudService.showUsers(term)),
    );
  }
}
<h2>Liste des utilisateurs</h2>
<ul>
  <li *ngFor="let user in getUsers() | async">{{ user.email }}</li>
</ul>
